import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide, useSwiper } from "swiper/react";
import { Autoplay, Pagination, Navigation } from "swiper";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import "swiper/css/autoplay";
import "./Home.css";  

// components
import { Navbar, Footer, Card } from "../../components";

// images
import {Console, HeaderImg, Ellipse, Left, Right, Playstation, Pc, Playstn, Laptop, Artwork, BigEllipse,  Union,  Delivery, Deposit, Light,  Esport,  Times,  Tech,  Lbb,  Learn,  Aadil,  Renuka,  Ammy,  Music,  Camera,  Apple,  Computers,  Show,  Head,  Scroll} from "../../assets";

const Home = () => {

  const swiper = useSwiper();
  const [showTopBtn, setShowTopBtn] = useState(false);
  useEffect(() => {
    window.addEventListener("scroll", () => {
      if (window.scrollY > 400) {
        setShowTopBtn(true);
      } else {
        setShowTopBtn(false);
      }
    });
  }, []);
  const goToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };
  



  return (
  < >
      <Navbar />

      {/* header section  */}
      <div className="headerContainer">
        <div className="headerContent">
        <div
          id="carouselExampleIndicators"
          className="carousel slide"
          data-bs-ride="carousel"
        >
          <div className="carousel-indicators" style={{marginTop: "70%"}}>
            <button
              type="button"
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide-to="0"
              className="active slideIndicator1"
              aria-current="true"
              aria-label="Slide 1"
            ></button>
            <button
              type="button"
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide-to="1"
              aria-label="Slide 2"
              className="slideIndicator2"
            ></button>
            <button
              type="button"
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide-to="2"
              aria-label="Slide 3"
              className="slideIndicator3"
            ></button>
          </div>
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img
                style={{ borderRadius: "15px" }}
                src={HeaderImg}
                className="d-block w-100 headerImg"
                alt="..."
              />
            </div>
            <div className="carousel-item">
              <img
                style={{ borderRadius: "15px" }}
                src={HeaderImg}
                className="d-block w-100 headerImg"
                alt="..."
              />
            </div>
            <div className="carousel-item">
              <img
                style={{ borderRadius: "15px" }}
                src={HeaderImg}
                className="d-block w-100 headerImg"
                alt="..."
              />
            </div>
          </div>
          <button
            className="carousel-control-prev"
            type="button"
            data-bs-target="#carouselExampleIndicators"
            data-bs-slide="prev"
          >
            
            <span className="visually-hidden">Previous</span>
          </button>
          <button
            className="carousel-control-next"
            type="button"
            data-bs-target="#carouselExampleIndicators"
            data-bs-slide="next"
          >
           
            <span className="visually-hidden">Next</span>
          </button>
          </div>
        </div>
      </div>


       {/* 
      catogeries section 
      1 */}

    <div className="catogeryHeading">
        <p>Select From Category </p>
    </div>
    <div className="catogeriesContainer">
      <div className="catogeriesContent">
      <div className="catogeryCard">
        <img src={Console} className="catImg" alt="Console" />
        <p className="catText">Gaming</p>
      </div>

      {/* 2 */}
      <div className="catogeryCard">
        <img src={Music} className="catImg" alt="Console" />
        <p className="catText">Music</p>
      </div>

      {/* 3 */}
      <div className="catogeryCard">
        <img src={Camera} className="catImg" alt="Console" />
        <p className="catText">Camera</p>
      </div>

      {/* 4 */}
      <div className="catogeryCard Apple">
        <img src={Apple} className="catImg apple" alt="Console"/>
        <p className="catText">Apple</p>
      </div>

      {/* 5 */}
      <div className="catogeryCard" >
        <img src={Computers} className="catImg" alt="Console" />
        <p className="catText">Computers</p>
      </div>

      {/* 6 */}
      <div className="catogeryCard showCard" >
        <img src={Show} className="catImg" alt="Console" />
        <p className="showText">ShowAll</p>
      </div>

      </div>
    </div>




    {/* Trending Now section */}
    <div className="trendingContainer">
        <div className="trendingContent">
        <h1 className="trendText">Trending Now!</h1>
        <div className="trendButton">
          <div className="leftButton" onClick={() => swiper.slidePrev()}>
            <img src={Ellipse} alt="Ellipse" />
            <img src={Left} alt="left" className="leftVector" />
          </div>
          <div className="rightButton" onClick={() => swiper.slideNext()}>
            <img src={Ellipse} alt="Ellipse" />
            <img src={Right} alt="left" className="rightVector" />
          </div>
        </div>
        </div>
      </div>

      <div className="cardContainer">
        <div className="cardContainerContent">
        
        <Swiper
          breakpoints={{
            500: {
              // width: "15.5rem",
              slidesPerView: 1,
              spaceBetween: 10,
              slidesPerGroup: 3
            },
            768: {
              // width: 768,
              slidesPerView: 3,
            },
          }}
          width={900}
          slidesPerView={3}
